import React from 'react'
import classname from 'classname'

import planeImg from '@lot-edge/assets/plane.svg'
import './Airplane.css'


export default class Airplane extends React.Component {

    state = {
        x: 0,
        y: 0,
        flying: false
    }

    // componentDidUpdate() {
    //     clearTimeout(this.timeout)
    //     this.setState({ flying: true })
    //     this.timeout = setTimeout(() => {
    //         this.setState({ flying: false })
    //     }, 1000)
    // }

    render() {
        const { flying } = this.state
        const { x, y } = this.props
        const scale = flying ? 1.5 : 1
        const style = {
            left: x,
            top: y,
            transform: `scale(${scale}, ${scale})`
        }

        return (
            <div className='airplane' style={style}>
                <img src={planeImg} style={{ transform: 'rotate(180deg)' }} />
            </div>
        )
    }
}