import React from 'react'

import './PromoBox.css'

export default function PromoBox({ background, title, price, city, description }) {
    return (
        <div className='promo-box' style={{ background: `url(${background})` }}>
            <div className='promo-box-content'>
                <h1 className='promo-box-title'>{title}</h1>
                <div className='promo-box-price'>
                    From <br />{price}
                </div>
                <div className='promo-box-summary'>
                    <h2>{city}</h2>
                    <p>{description}</p>
                </div>
            </div>
        </div>
    )
}